css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tvguide.co.uk") {
    
/* remove surplus elements */
div#divHighlights_top {display: none !important;}
body > table:nth-child(10) {display: none !important;}
body > div:nth-child(12) {display: none !important;}
div a.twitter-follow-button {display: none !important;}
div.top_logo {display: none !important;}
div#rightcolumn {display: none !important;}
table#tabs {display: none !important;}
div.top_nav {display: none !important;}
body > table:nth-child(9)  {display: none !important;}
body > div:nth-child(1)  {display: none !important;}
body > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1)  {display: none !important;}
body > table:nth-child(10) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > table:nth-child(1) {display: none !important;}
body > table:nth-child(10) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(5) {display: none !important;}
div#tvnews1 {display: none !important;}
body > div:nth-child(10) {display: none !important;}
body > div:nth-child(11) {display: none !important;}
/* take out background image */
body {background-image: none !important;}
/* remove twitter follow elements */
#tdLeftMenu > div:nth-child(5), #tdLeftMenu > div:nth-child(6) {display: none !important;}
/* remove adverts */
iframe {display: none !important;}
#tdLeftMenu > div:nth-child(3) {display: none !important;}
/* supressing adtech.de imput - looks like advertising */
    body > table:nth-child(10) {display: none !important;}
    
/* adjust width of left panel on-toggle */
td#tdLeftShow {width: 20px !important;}

}
