css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.org"), domain("www.thepiratebay.org/"),
url("http://thepiratebay.org/") 

{

#content  {background: #222 !important; -moz-border-radius: 20px !important; border: solid 10px #222 !important;}


[src="http://static.thepiratebay.org/img/bar.gif"],.spons-link,[src^="http://platform0.twitter.com"]{display:none!important}

#TPBlogo{background:url(http://i13.photobucket.com/albums/a261/ericparsons/tpbtape-1.png)!important; width: 0px !important; height:70px !important; padding-left: 90px!important}

h1 a{background:url(http://i13.photobucket.com/albums/a261/ericparsons/Image1.png)!important;width:310px!important; height: 350px !important} 



a:link    { color: #ccc !important}
a:visited { color: #999 !important}
a:active  { color: #999 !important}
a:hover   { color: #ccc !important}
span  {color: #ccc !important}
.entrybody p {color: #999 !important}
.entrybody ul {color: #666 !important}
.entrymeta {color: #777 !important}
.commenttext {color: #999 !important}
B {color: #999 !important}
LABEL {color: #999 !important}
DT {color: #999 !important}
DD {color: #999 !important}
TD  {color: #777 !important}

#main-content > IFRAME {height: 0px !important;}

#main-content{ margin-left:60px !important;  margin-right: 50px !important}

a {border: none !important}

BODY {background: #111 !important;}

H1   {background: #222 !important; -moz-border-radius: 20px !important; border: solid 10px #222 !important}

H2 {background: #222 !important; -moz-border-radius: 20px !important; border: solid 6px #222 !important}

IMG[border="0"][alt=""][src="http://static.thepiratebay.org/img/bl2009xmas.png"] {display: none !important}

IMG[border="0"][alt=""][src="http://static.thepiratebay.org/img/blTrial.gif"] {display: none !important}

P {background: #222 !important; -moz-border-radius: 20px !important}

TH {background: #333 !important;}

table#searchResult th, table#searchResult td, #searchResult  {border: solid 1px #111 !important; }

#searchResult > TBODY > TR {background: #222 !important; border: solid 2px #111 !important}

#searchResult > TBODY > TR:hover {background: #333 !important; border: solid 2px #111!important}

#title {background: #333 !important; border: none !important; -moz-border-radius: 14px 14px 0px 0px !important}

#detailsframe { border: none !important; -moz-border-radius: 18px !important}

#details {background: #222 !important;}

.nfo {background: #666 !important; border: solid 2px #111 !important}

IMG[border="0"][alt="Toolbar"][src="http://static.thepiratebay.org/img/new_toolbar.gif"] {display: none !important}

#commentsheader > H4 {color: #999 !important;}

.byline {color: #777 !important;}

.comment {background: #666 !important; border: solid 6px #666 !important;}

DD, DT { border-bottom: solid 2px #333 !important}

#details > A > IMG {display: none !important}

}
