css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.nsks.com/smash_contents/racket_impression/") {
/* Non-display part */
#header_wrapper,
#side_bar,
#footer_outside
{ display: none !important; }

/* Display part */
body,
#smash_contents,
#outside,
#wrapper_page,
#wrapper_page_inner,
#wrapper_main,
#bread_crumb_box,
#wrapper_main_inner,
#contents,
.text_box
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

#magazine_title
{
    width: auto !important;
    background: #000000 !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

}
