@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sceneaccess.org") {
	body {
		color: #000000 !important; 
		background-color: #405a69 !important;   
	}

	table {
		background-color: #749db1 !important;
	}
	
	table.bottom {
		background-color: #688ea3 !important;
	}
	td {
          border-color: #000000 !important;
        }
	td.colhead {
	  background-color: #425C69 !important;
           border-color: #000000 !important;
	}
	
	a:link, a:visited {
	  color: #000000 !important;
	}

	a:hover {
	  color: #A8D3FF !important;
	}
	a.sort {
		color: #ffffff !important;	
		text-decoration: none !important;
	}
	a.sort:hover {
		text-decoration: underline !important;
	}
}
