css:
/* kubalibre.ru -- clean and wide pages v0.1
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kubalibre.ru") {

.wrapper{ width: 100% !important; }

div.searchform{
top: 120px !important;
left: -70% !important;
width: 480px !important;
z-index: 2;
}

/* fix search bar */
.toolbar{ top: -80px !important; z-index: 1; }
#searchinput{ width: 86% !important;}
#tophead { height: 10px !important; }

/* no banner! */
#banner{ display:none !important; }

/* Remove chat-rules */
td[colspan="2"][width="100%"]{ display:none; }
/* chatbox height*2 */
#shoutbox{ height: 700px !important; }

/* Makes pageswitchers more clickable */
a[onclick*="pageswitcher("]{
padding-left: 20px !important;
padding-right: 20px !important;
}

}
