css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("4chan.org")
 
{body[bgcolor] {
background-color: #000 !important;
background-image: none !important; 
color: #00bb00 !important;
font-weight: bold;}
a {font-weight:bold;}

div#logo {background: url(http://i.imgur.com/AGnbhrP.png) !important; background-size: 240px !important;  background-repeat: no-repeat !important;}

* {border: none !important;}
.yotsuba_b_new #quote-preview.highlight, div.opContainer, .preview, .reply:target, .reply.highlight, .reply, .reply:hover, * {border-color: #00ff00 !important;}
.reply:target, .reply.highlight {border-width: 2px !important; border-style: solid !important;}

body, #content, #settingsMenu, td.postblock, tr, div.extPanel, strong, span.button, div#theme.panel {color: #00bb00!important; background-color: #000 !important; background-image: none !important;}

div.post, div.post-body, div.post.reply, .dd-menu li { background-color: #131313 !important; }

#arc-list .quotelink:hover, div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover, .boardnav .pageJump a, .mFileInfo, div.postLink span.info, a:hover, a.replylink:not(:hover),  div.post div.postInfo span.postNum a:hover, .tu-error, s:hover a, #qrError a, #qrError a:hover,  .yotsuba_b_new .backlink a:hover {color: #00ff00 !important; }

s:hover {color: #00ff00 !important; background-color: #333 !important; background-image: none !important;}
s { color: #333333 !important; background: #333333 !important; text-decoration: none;}

div#boardNavDesktop {width: 99%;text-align: center;padding: .4em;}

 
div.rc-anchor.rc-anchor-light {background-color: #000 !important; } 
  
div.boardSelect > strong, .stylechanger, div#boardNavDesktopFoot, div#absbot, div.postingMode, div.boardBanner, div.stylechanger, tr.rules, li.fill {display:none !important;}

div#boardNavMobile select, pre.prettyprint {border-style:solid !important; border-width:2px !important; border-color: #00bb00 !important; background-color: #888 !important;}

span.pln {color: #fff !important}

#arc-list .quotelink, div.post div.postInfoM span.postNum a, div.postInfo span.postNum a , div.post div.postInfoM span.dateTime, .burichan_new .backlink a, .yotsuba_b_new .backlink a, span.summary, span.name, span.subject, span.postertrip, a, div, td, dt, span.abbr, a.quotelink, span.deadlink, .posteruid, .hand:hover, div.post div.postInfo span.nameBlock span.name, input, div.post div.postInfo span.nameBlock span.postertrip {color: #00bb00 !important;}


.recaptchatable #recaptcha_response_field:focus, input[type="text"]:focus, textarea:focus { border-color: #00bb00 !important }

td, div, li, H3, html, hr, span.abbr  {background:transparent !important;}

 .recaptchatable #recaptcha_response_field {border-color: #000 !important}
 div.postLink a, div.postLink a:hover, .button, div#boardNavMobile select, .recaptchatable, #recaptcha_area tr, #recaptcha_area td, #recaptcha_area th, input, textarea, input[type="submit"], input[type="button"], input[type="file"], input[type="checkbox"], input[type="password"]:focus, #postFile {color: #00bb00 !important; background-color: #000 !important; border: 1px solid #131313 !important;;}

input[type="submit"], input[type="button"], input[type="file"], td {font-size: 8pt}

.newPostsMarker:not(#quote-preview) {box-shadow: 0px 3px #00ff00;}

select#size-ctrl, select#order-ctrl, select#teaser-ctrl {color: #00ff00; background-color: #131313;}
   
}
/* N0Lif3 v2 rework update 2009-2014 */
