css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("karapaia.livedoor.biz") {

.ad_content {
 display:none !important; 
}

div#karapaia_top 
{
 position:relative; z-index:1 !important; 
}

div.subspace
{
 position:relative; z-index:99 !important;
}
 
iframe[id*="ads"],
div[id*="plugin"],
div#footer,
div[class*="ad"],
div.topics,
div.PRz,
div.topbar,
div.amazlet-box,
div.related-articles,
div#acrp2_karapa table
{
 display:none !important;
}

div.commentttl
{ 
 border-top:1px solid #fff;
}

div.commenttext
{
 background-color:#fff;
}

div.main
{
 background-color:#fff;
 padding:1ex;
 margin:.5ex;
 border:1px solid #333;
}

#mainspace,
#content,
.article-pager,
#blogcontainer,
#container
{
 width:98.5% !important;
}

img[src*="e363fcf9.gif"]
{
 width:32px;
 height:32px;
}

#container
{
 margin-left:1em !important;
 margin-right:1em !important;
}

}

/* ads cut */
@-moz-document domain("ad.adlantis.jp"){
body
{
 display: none !important;
}

}
