{
form {
-moz-transform: rotate(8deg) translateY(10px);
}
br + font {
-moz-transform: rotate(-5deg) translateY(50px);
}
img[src="/intl/en_ALL/images/logo.gif"] {
-moz-transform: rotate(-15deg) translate(150px,12px);
}
p > font {
-moz-transform: rotate(60deg) translate(40px,90px);
}
.gac_m[style="visibility: hidden; left: 390px; top: 271px; width: 485px;"] {
