css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.reuters.com/article/)
{
	/* Hide banner ads, left and right sidebars. */
	.grid1, .grid2, .grid3, .grid4, #networkFooterHeader, #moreFromReuters, #footerHeader, #disclaimer, #trackbar.active { display: none !important; }

	.gridlined4 { background: none !important; }

	/* Resize image. */
	img { max-height: 250px !important; }

	.grid8 { width: 950px !important; }
}
