css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://omegle.com/") {
 p, div, form, h2,  body { color: lightgray !important; background: black !important }
a, a:link, a:hover {color:white !important;}
.logbox {visibility:hidden !important;}
.logbox a, .logbox a:link, .logbox .youmsg, .logbox .strangermsg, .logbox input {visibility:visible !important;}
.youmsg {color:lightblue !important;}
.youmsg .msgsource {color:blue !important;}
.strangermsg {color:#ffaaaa !important;}
.strangermsg .msgsource {color:#ba0000 !important;}
.logitem input {color:black !important; }
img {visibility:hidden !important;}
#chatbutton img {visibility:visible !important;}
.controlwrapper input {color:white !important; background-color:black !important;}
.statuslog {visibility:visible !important;}
.chatmsg {color:red !important;}
}
