css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("1000stipendii.org") {
  .edit-icon, .edit-icon a {
     background-image: url(http://www.phpbb.com/community/styles/prosilver/imageset/en/icon_post_edit.gif) !important;

  }

  .quote-icon a {
     background-image: url(http://www.phpbb.com/community/styles/prosilver/imageset/en/icon_post_quote.gif) !important;

  }

  ul.profile-icons li.quote-icon a {
    height: 20px !important;
    width: 54px !important;
  }
 
  ul.profile-icons li.edit-icon a {
    height: 20px !important;
    width: 42px !important;
    margin-right: 10px !important;
  }
  
  ul.profile-icons li.edit-icon a:hover, 
  ul.profile-icons li.quote-icon a:hover {
    background-position: 0 -20px !important;
  }
}
