css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://japanese.engadget.com/") {
#subcontent,
div[id*="p"] table,
.comments,
#addcomments,
#commentform,
div[id*="wow_"],
#footer,
#grid,
#slice,
.topleader,
.bottomleader,
.postnav,
.footer-cobrand,
.cobrand,
#network,
#header,
#hat,
a[href*="ad."]
{
 display:none !important;
}

.posttags
{
 border-top:3px double #DFDFDF !important;
 margin-top:2em !important;
 text-align:right !important;
}

body
{
 background: url(http://www.blogsmithmedia.com/japanese.engadget.com/media/eng-jp-logo-t.png) no-repeat 15px -45px;
}

#content
{
 background: none !important;
 margin-right:2em !important;
}

#page
{
 max-width:100% !important;
 min-width:1% !important;
}

.posttitle,
.byline,
.post h2
{
 text-align:right !important; 
}

.posttitle
{
 color:#14B1DF !important; 
}

div[align="center"] img,
div[style*="center"] a img
{
 border: 1px solid #DFDFDF !important;
}

}
