css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neatorama.com") {

strong + table,
.post + br + table, br, body > table > tbody > tr > td,
#footer,
#comment + br + div
#header-bannerad, #header-textad,
.clearboth + table,
#commentform p:first-child,
#outbrain_container_0_bottom
{ display:none !important; }

body > table > tbody > tr > td[width="520"] { display:block !important; }

#header { height:auto !important; }
.clearboth, #header-bannerad { height:0 !important; }
#content { margin:auto !important; width:600px !important; }

object, center > img, [align=center] > img { display:block !important; }

}
