css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cozi.com") {

.FamilyFont,
a.FamilyFont,
a.FamilyFont:hover {display: none !important;}
.FamilyPhoto {display: none;}
#topPane {background: none !important; background-color: #eee!important;}
.LeftPane {top: 12px !important; position:absolute !important;}
#footerPane.footer div.wrapper {background: none !important; background-color: #999!important;}
#journalPromptArea {display: none !important;}
div.HomeButton {display: none !important;}
div #leftPane { background: none !important; background-color: #eee!important;}
.MonthView .CalendarDay .DayOfMonthNumber {font-size: 30px !important;}
.MonthView .CalendarDay {border-right: 1px dotted #ffd570 !important;}
#listHeader {height:60px !important;}
#listHeader .ReadOnly, #listHeader .ReadWrite input  {font-family: Calibri !important; font-size: 30px !important;}
.MiniListButton {font-family: calibri !important;}


}
