css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("teuxdeux.com") {

ul.list_items, body, #deux_list, #someday_list, #footer, ul.list_items li p.teuxdeux:hover  { 
background: darkOliveGreen !important;
}

body {color: Cornsilk !important;}
#deux_list .red, h4 {color: Maroon !important;}
#header, .header {background: Maroon !important; color: cornsilk !important;}
.daily_list input {background: CadetBlue !important;}
.daily_list {border-left: 1px solid SeaGreen !important;}
#deux_list div.last, #someday_list .list_items {border-right: 1px solid SeaGreen !important;}
#someday .header h2 {color: cornsilk !important;}
#deux_list {border-bottom: 5px solid darkOliveGreen !important;}
#footer {margin-top: 0px !important;}
ul.list_items li p.teuxdeux {border-bottom:1px dashed SeaGreen;}
}
