css:
@-moz-document domain(wikipedia.org),

domain(wikimedia.org),

domain(wikinews.org),

domain(wikibooks.org),

domain(wikiquote.org),

domain(wikiversity.org),

domain(wikisource.org),

domain(wiktionary.org){

p, div, table, form, li, tr, td, h2, body {color:#99CCFF !important; background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important }
h5 {color:#FF8800 !important;}

a, a:link {color:white !important;}
#p-cactions div ul li a:hover {color:white !important; background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important }
#p-cactions div ul li a {color:blue !important;}
.wikitable th a {color:black !important; opacity: 0.5 !important}
.prettytable th a, .prettytable th a:link, .prettytable th a:hover {color:black !important;}
#collapsibleTable0 a.image img, .thumbinner a.image img {background: #FFFFFF fixed repeat left top !important;}


#p-cactions div ul li.new a {color:red !important;}
.wikitable th a.new {color:red !important; opacity: 0.5 !important}
a.new, a.new:hover, a.new span, a.new span:hover {color:#FF0000 !important;}

.prettytable th, .wikitable th {color:black !important; opacity: 0.5 !important}
div.tright {border-width: 0 0 0 0 !important; background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important;}
.diff-deletedline div {color:black !important; background: #FFFFAA none repeat scroll 0 0 !important}
.diff-addedline div {color:black !important; background: #CCFFCC none repeat scroll 0 0 !important}
#collapsibleTable0 th {background: #222222 fixed repeat left top !important;}

pre, code {background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important; color: #000000 !important;}

.sinossifumetti caption, .sinossifumetti th, .sinossifumetti span {background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important; color: black !important;}

#filetoc {border-width: 0 0 0 0 !important; background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/tech3.jpg) #222222 fixed repeat left top !important}

img {opacity:0.7 !important;}

#siteNotice p {color:black !important}

}
