css:
/* Shoutcast cleanup */
@-moz-document domain("shoutcast.com") {
  img[src="/images/aolmusic_black.gif"],	/* aol music logo */
  table[width="100%"] td + td[nowrap],		/* current statistics */
  body > table > * > tr + tr,			/* nav link bar */
  body > table + table,				/* second table (big banner ad on right) */
  body > table + br + table,			/* shoutcast showcase (main page only) */
  table + table.highlighttable,			/* bottom search/genre box */
  .SearchBox,					/* right bar */
  body > img,					/* bottom blue bar */
  body > center					/* footer (blue bar, nullsoft logo, etc.) */
    { display: none !important }
}
