@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("code.google.com") {
  @media print {
    #gaia, #footer, #mt, #wikimeta, .st, .gbh, .hostedBy, form[action='/hosting/search'] { display: none !important; }
    #wikicontent table { width: 100% !important; }
    .vt { padding: 5px !important; margin: 5px !important; }
    #wikicontent p, #wikicontent ul, #wikicontent ol, #wikicontent blockquote { max-width: 90% !important; }
  }
}
