css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamedesignadvance.com")

{

 body
 {
  background:#38404D none repeat scroll 0 0 !important;
  color:#FFFFFF !important;
 }

 a:link
 {
 color: #4A77FF !important;
 }

 a:active
 {
  color: #B0C5FF !important;
 }

 a:hover
 {
  color: #D82020 !important;
 }

 #sidebar h2
 {
  border:medium dotted !important;
  color:#B38486 !important;
  padding-bottom:1px !important;
  padding-left:0px !important;
  padding-top:0px !important;
 }

 .commentlist .alt
 {
  background:#5D5D72 none repeat scroll 0 0 !important;
  border: #FFFFFF dashed 1px !important;
 }

 #footer
 {
  background:#262C35 none repeat scroll 0 0 !important;
  /* color:#CCCCCC !important; */
  border: #FFFFFF dashed 1px !important;
 }

 blockquote
 {
  background:#5D5D72 none repeat scroll 0 0 !important;
  border:1px dashed #DDDDDD !important;
 }

}
