#maintable,.postTitle,.postText,body,#main,#ctl04_comment_form {
    background-color:#000 !important;
    color:#88c !important;

}
html{
    background-color:#000 !important;
}
.postTitle a {
    color:purple !important;
    font-size:1.4em;

}
.postText p {
    font-size:1.2em;
    color:#88c;
}
#header {
    background-color:#000 !important;
    border:1px dashed purple;
}
