css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

img{max-height:10px!important; max-width:10px!important;}
img:hover{max-height:100%!important;max-width:100%!important;}
.damncr-title, .damncrc-topic{height:15px!important; overflow:hidden!important;}
.damncr-title:hover, .damncrc-topic:hover{height:auto!important;}
.damncrc-topic img{display:none!important;}


strong{font-weight:normal!important; font-style:italic!important;}
strong a{font-style:normal!important;}
#top {height:20px!important;padding-top:5px!important;}
.damncri-member img{max-height:50px!important;max-width:50px!important;}

.shadow{background-image:none!important;}
.damncr .msg {padding:1.5px!important;}


.damnc-tabbar strong, .damnc-tabbar a{padding:1px 5px 2px!important;margin-right:2px!important;margin-bottom:-2px!important;}
.damn-lo-row-top{min-height:10px!important;}
.damnc-tabbar strong{padding-bottom:4px!important;}

.damnc-tabbar strong i, .damnc-tabbar a i{display:none!important;}
.damnc-tabbar strong img{display:none!important;}


}
