@-moz-document domain("reference.com") {

#main {
	width:100% !important;
}

#mainbody {
	width:100% !important;
}

#leftRail, .spl_unshd_NC, #btmform, .addBorder, #v2, .footerpadding, #foot_results {
	display: none;
}

.lhdiv {
	width: 20px !important;
}

