css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabs-newtab-button {
list-style-image: url("chrome://browser/skin/Toolbar-small.png")!important;
-moz-image-region: rect(0px, 54px, 20px, 36px) !important;
max-height: 26px !important;
margin-right: 0px !important;
margin-left: 0px !important;
}

.tabs-newtab-button:hover {
-moz-image-region: rect(52px, 54px, 75px, 36px) !important;
}

.tabbrowser-tabs tab {
max-height: 26px !important;
}

.tabbrowser-tabs tab:last-child {
margin-right: 0px !important;
}

.tabbrowser-tabs tab:first-child {
margin-left: 0px !important;
}
