@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {

/* This is for the Top Banner (2010) */
#top_ad_wrapper {
  visibility: hidden !important;
  position: relative !important;
  height: 25px !important;
  top: -125px !important;
  }
/* This is for the Top Banner (11-2011) */
.top-rhs { display: none !important; } /* #top_rhs_wrapper */
/* We change the background of the Top container (2010) */
#nb20.supertab #supertab {
  background-color: #231F20 !important; /*#0F1319*/
  }

/* Center right big banner (2010) */
#tn15adrhs { display: none !important; }

/* Middle page banners (2010) */
.sponsored_links { display: none !important; }
/* Middle page banners (11-2011) */
.middle-rhs { display: none !important; }

/* And finally the bottom one (2010) */
#bottom_ad_wrapper { display: none !important; }
/* And finally the bottom one (11-2011) */
.bottom-rhs { display: none !important; }

}
