css:
/* - Ensure the chevron is only the icon - */
toolbarbutton.chevron > .toolbarbutton-text,
toolbarbutton.chevron > .toolbarbutton-menu-dropmarker {
  display: none !important;
}

/* - Fix Bookmarks Toolbar large icons mode - */
#navigator-toolbox #PersonalToolbar[iconsize="large"] > image {
  max-height: 32px !important; 
  max-width: 32px !important; 
}

#PersonalToolbar[iconsize="large"] toolbarbutton.chevron > image {
  max-height: 22px !important; 
  max-width: 22px !important; 
  background-image: none !important; 
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABMAAAAWCAYAAAAinad/AAAALUlEQVR42mNgGAWjYBTQCqSlpf0HYWL5RBkGBKuJ4RMEIIXIignxR8EooCcAAG0wOzXQicwkAAAAAElFTkSuQmCC") !important;
  padding: 0px 1px 0px 1px !important;
  margin: 2px -5px -1px -5px !important; 
}

#PersonalToolbar[iconsize="small"] toolbarbutton.chevron image { /* UNTESTED */
  background-image: none !important;
  list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABMAAAAWCAYAAAAinad/AAAALUlEQVR42mNgGAWjYBTQCqSlpf0HYWL5RBkGBKuJ4RMEIIXIignxR8EooCcAAG0wOzXQicwkAAAAAElFTkSuQmCC") !important;
  padding: 4px 1px 0px 1px !important;
  margin-top: 3px !important; 
}
