css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phoronix.com") {

div#phxcms_toplogo_top, 
div#phxcms_top_leaderboard, 
div.phxcms_nav_int, 
div.phxcms_copyright, 
div#phxcms_content_phx > div.phxcms_normal_format div:first-child > div:first-child, 
div#phxcms_content_phx > div[style='text-align: left; overflow: hidden; width: 601px;'] > div[style='width: 305px; float: right;'], 
div#phxcms_content_phx > div.phxcms_normal_format > div[style='padding: 0 4px 5px; float: right;'], 
div.phxcms_navigation_format[style='text-align: left; font-weight: bold; font-size: 14px;'], 
div.phxcms_contentphx_right_bar:first-child {
    display: none !important;
}

div#phxcms_content_phx {
    min-height: 526px !important;
}

div#phxcms_toplogo {
    height: 115px !important;
}

div#phxcms_midstrip {
    text-align: right !important;
}

div#phxcms_part_super {
    height: 75px !important;
}

div#phxcms_toplogo_bottom {
    margin: 50px 0 0 0 !important;
}

div[style='float: left; vertical-align: top; width: 295px;'] {
    width: auto !important;
}

div#phxcms_content_phx > div[style='text-align: left; overflow: hidden; width: 601px;'] > div[style='width: 295px; float: left; vertical-align: top;'] {
    width: auto !important;
}

}
