css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

tab.tabbrowser-tab, tab.tabbrowser-tab:hover {
    direction: ltr !important;
    margin-left: 0pt !important;
    margin-right: 0pt !important;
    padding-left: 0pt !important;
    padding-right: 0pt !important;
}

tab.tabbrowser-tab:hover, tab.tabbrowser-tab > .tab-text {
    margin-right: 0px !important;;
}
tab.tabbrowser-tab > .tab-icon-image, tab.tabbrowser-tab:hover .tab-icon-image {
    visibility:visible !important;;
    width:16px !important;
    margin-left: 2px !important;
}
.tabbrowser-tab .tab-close-button, .tabbrowser-tab:hover .tab-close-button {
    margin-left: 0px !important;
    margin-right: 0px !important;
}
.tabbrowser-tab:hover .tab-close-button, .tabbrowser-tab[selected='true'] .tab-close-button
{
    visibility: visible !important;
    -moz-box-ordinal-group: 4 !important;
}
.tabbrowser-tab > .tab-text {
    margin-left: 1px !important;
}
