css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#find-field,
.findbar-textbox
{
width: 250px !important;
}

#find-next, .findbar-find-next.tabbable,
#find-previous, .findbar-find-previous.tabbable,
#highlight, .findbar-highlight.tabbable,
#find-invert
{
width: 24px !important;
}

#find-next > .toolbarbutton-icon, .findbar-find-next.tabbable .toolbarbutton-icon,
#find-previous > .toolbarbutton-icon, .findbar-find-previous.tabbable .toolbarbutton-icon,
#highlight > .toolbarbutton-icon, .findbar-highlight.tabbable .toolbarbutton-icon,
#find-invert > .toolbarbutton-icon
{
margin: 0 !important;
}

#find-next > .toolbarbutton-text, .findbar-find-next.tabbable .toolbarbutton-text,
#find-previous > .toolbarbutton-text, .findbar-find-previous.tabbable .toolbarbutton-text,
#highlight > .toolbarbutton-text, .findbar-highlight.tabbable .toolbarbutton-text,
#find-invert > .toolbarbutton-text
{
display: none !important;
}

#find-case-sensitive
{
padding: 0 !important;
}
