css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ogame.ru") {
    div[id*="eventRow"] li.countDown { display: none !important; }
    div[id*="eventRow"] li.descFleet { display: none !important; }
    div[id*="eventRow"] li.originFleet { display: none !important; }
    div[id*="eventRow"] li.destFleet { display: none !important; }
    div[id*="eventRow"] li.sendProbe { display: none !important; }
    div[id*="eventRow"] li.sendMail { display: none !important; }
    div[id*="eventRow"] li { top: 0px !important; }
    div[id*="eventRow"] { height: 24px !important; }
}
