@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain(netlog.com) {

    /* hide google search & friend invite */
    form.contentSideBox,
    #friendInviteTrigger,

    /* hide beta badges */
    span.betaBadge,

    /* hide news, brands, polls & contests */
    .PersonalHomePageNewsComponent,
    .PersonalHomePagePagesComponent,
    .PersonalHomePageFeaturedPollComponent
    .PersonalHomePageContestsComponent,
    .componentThemeRed,
 

    /* hide ads */
    .ad { display: none !important }
    .homeNews{ display: none !important }
}


#hud

{

background: none !important;

-moz-box-shadow: none !important;

}



#hud .hudButton

{

background:url("http://fc00.deviantart.net/fs45/f/2009/138/3/1/Light_Leather_Desktop_by_reeks.png") repeat-x scroll left top ;

-moz-box-shadow: 0.9px 0.9px 85px 0.3px rgb(255,250,250)!important;


}
