@-moz-document domain("www.thefashionspot.com") {
img { max-width: 100%; }
iframe, layer, noscript, embed { display: none; }
body>div>div>div { margin: 0 !important; padding: 0 !important; width: auto !important; }
body>div>div>div>* { display: none; }
body>div>div>div>table { display: table; }
body>div>div>div>table.tborder + div { display: block; }
body>div>div>div>table>tbody>tr>td:first-child + td { display: none; }
body>div>div>div>table>tbody>tr>td:first-child + td + td + td { display: none; }
body>div>div>div>table>tbody>tr>td:last-child { display: none; }
body>div>div>div>table>tbody>tr>td.alt2 { display: none; }
body>div>div>div>table>tbody>tr>td:first-child { display: table-cell !important; }
body>div>div>div>table.tborder>tbody>tr>td.alt2 { display: table-cell !important; }
body>div>div>div>table>tbody>tr>td>img { display: none; }
body>div>div>div>table>tbody>tr>td>div>a { display: none; }
body>div>div>div>table>tbody>tr>td>div.navbar>a { display: inline; }
body>div>div>div>table>tbody>tr>td>div>img { display: none; }
body>div>div>div>table>tbody>tr>td>table table { margin: 3px 0 0 0; }
body>div>div>div>table>tbody>tr>td>div>div>div>* { display: none; }
body>div>div>div>table>tbody>tr>td>div>div>div>div:first-child { display: block; }
body>div>div>div>table>tbody>tr>td>div>div>div>table:first-child { display: table; margin: 3px 0; }
body>div>div>div>table>tbody>tr>td div { margin: 0 !important; padding: 0 !important; width: auto !important; }
body>div>div>div>table>tbody>tr>td>div>div>div>div>div>* { display: none; }
body>div>div>div>table>tbody>tr>td>div>div>div>div>div>table:first-child { display: table; }
}
