css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
#status { font-family:malgun gothic !important; font-size:16px !important; padding:10px !important; width:92% !important; height:100px !important; margin-bottom:10px !important; margin-top:10px !important; }
#text { font-family:malgun gothic !important; font-size:16px !important; padding:10px !important; width:92% !important; height:100px !important; margin-bottom:10px !important; margin-top:10px !important; }
.entry-content { font-family:malgun gothic !important; font-size:14px; line-height:1.3; text-align:justify; }
.profile-head h2 { font-family:Georgia !important; font-size:48px !important; font-weight:normal }
.photo { width:25px !important; height:25px !important; }
li.status { padding:15px 0 15px 0 !important; border-bottom:1px solid #dddddd !important; }
}
