css:
 @namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.ifeng.com/),url-prefix(http://www.zaobao.com/),url-prefix(http://news.ifeng.com/),url-prefix(http://dict.youdao.com/),url-prefix(http://realtime.zaobao.com/),url-prefix(http://finance.ifeng.com/),url-prefix(http://ent.ifeng.com/),url-prefix(http://bbs.news.163.com/),url-prefix(http://blog.ifeng.com/),url-prefix(http://news.163.com/),url-prefix(http://www.dahe.cn/),url-prefix(http://www.bbc.co.uk/zhongwen/),url-prefix(http://news.backchina.com/) {
   html, body, tbody, tr, td, div { background-color: #1b222b !important; color: #99BBFF !important }
   pre {
      display: block !important ; 
      background-color:#1b222b !important ;
      border: #1b222b solid 0.1em !important ;
      color: #1b222b !important ;
      font-family: "courier" !important ;
      margin:  1em 2em 2em 1.3em !important ;
      overflow: auto !important ;
      padding: 0.5em 2em 1em 1em !important ;
      text-align: left !important ;
      white-space: pre !important ;
      font-family: "courier" !important ;
   }

   /* defaults for all links */
   a:link, a:visited { color: #acb7b1 !important } 
   a:active { font-weight: bold !important }
   a:hover { color: #007700 !important }
   h1, h2, h3, h4, h5 { color: #FFFAFA !important }

   .tborder { border-width: 0 !important }
   span, i { color: #acb7b1 !important }
   sup { color: #1b222b !important }
   td.bannertop { display: none !important }
   div.actions,
   div.closeWords,
   p,
   table#searchi,
   table.layout,
   td.tcat,
   td.thead { background-color: #1b222b !important ; background-image: none !important }
   tr.even * { background-color: #1b222b !important }
   tr.odd * { background-color: #1b222b !important }
}
