css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tomshardware.com") {

div#header { height: auto !important; }

a.comment, div#commentsBox, div#footer, div#header-advert, div#internalMain > div.basicShadow + div.basicCentral + div.basicCentral, div#internalSidebar, div#news-content > a[style="float: right;"], div#news-content > br, div#news-content > div.greyBox.clearfix, div#popinMailAmi, div#web2Tools, form#commentForm, li.toolbarNews-comment, li.toolbarNews-send, ul.simplePagination.clearfix, ul#header-sections2 > li:last-child { display: none !important; }

div.basicShadow, div.basicShadow > div.in2 { background: #fff !important;}
div.basicShadow > div.in2 { padding:9px !important}
div.clearfix[style="margin-top: 10px;"] { margin-bottom: 0 !important; }
div.basicCentral-elm {width:480px !important; margin-top:20px !important}
.condensedVision li p { float:right; width:300px !important;}
div.basicCentral, div#internalMain, div#main { height: auto !important; width: auto !important; }

}
