css:
@-moz-document domain("engadget.com") {

div, p, a, span, label {
  font-family: Arial, Helvetica, sans-serif !important;
}

.post_title a {
  font-size: 20px !important; 
  font-weight: bold;
}
.post_body {
  font-size: 12px !important;
}
.my-little-hero {
  display: none !important;
}
.topstories {
  display: none !important;
}
#comments .comment_data .comment_date {
  font-size: 12px !important;
  line-height: 20px;
  font-style: normal !important;
  color: #666666 !important;
  float: left;
  display: inline;
  margin: 0 10px 0 0;
}
.caption {
  font-style: normal !important;
  font-size: 12px !important;
}
.post_source a, .post_via a,
.filed_under a, .post_show_tags a {
  font-style: normal !important;
}
#spherePoweredBy, .sponsorArea {
  display: none !important;
}
.sphere a {
  font-style: normal !important;
  font-size: 12px !important;
}
.sphere_timestamp {
  font-style: normal !important;
  font-size: 11px !important;
}
.comment_date a {
  font-size: 12px !important;
}
.badComment_msg {
  font-size: 12px !important;
  font-style: normal !important;
}
.notLoggedIn {
  font-style: normal !important;
}
.poll label {
  font-style: normal !important;
}
}
