css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fmatalk.com") {

div#sidebar_container{
  width: 100% !important;
}

div#content_container, div#content_container div#content{
  margin-right: 0px !important;
}

.forumbit_nopost .forumbit_nopost .forumrow .forumdata, .forumbit_post .forumrow .forumdata {
font-size:12px !important;
}

.threadbit .nonsticky, .threadbit .discussionrow {
background:none repeat scroll 0 0 #FFFFFF !important;
}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
background:#FFFFFF !important;
}

.forumdescription img{
  margin: 5px;
}

.forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .wgo_block{
  -moz-box-shadow: 0px 0px 0px white !important;
}

ol#threads .threadbit .alt {
background: white!important;
}

a#sidebar_button_link{
  visibility: hidden;
}

a.smallavatar {
float:none !important;
}

div.smallavatartext {
float:none;
margin-right:0px;
margin-top:0px  !important;
width: 880px !important;
}

#sidebar_container > ul {
-moz-box-shadow:0px 0px 0px white !important;
}

}
