css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#extensionsView>scrollbox { background-color:#F4EEDE; }
#extensionsView extension>hbox { background-color:#E1D4BD!important; border: 2px -moz-bg-outset #A28662; }
#extensionsView extension[selected="true"]>hbox { border-style:-moz-bg-inset; background-color:#F4EEDE!important; }

#extensionsView box, #extensionsView extension>hbox { display:block !important; }
#extensionsView box { padding:8px; }

#extensionsView extension { width:110px !important; height:110px !important; padding:0 !important; background-image:none !important; }
#extensionsView extension>hbox { width:110px !important; height:110px !important; margin:2px !important; }
#extensionsView stack { left:5px !important; position:relative; top:0px; }
#extensionsView label { width:92px !important; padding-top:10px !important; top:-5px !important; position:relative; }
