css:
@-moz-document domain(witze.net)
{
 .a-north,
 .a-east,
 .a-south,
 .a-west,
 .jokeA-subContentCommercial,
 .jokeA-subContentBar,
 .jokeA-navBar,
 .jokeA-mainContentTop+table+table,
 .jokeA-mainContentTop tr+tr hr
 {
  display:none !important;
 }
 body
 {
  min-width:10px !important;
 }
 h1
 {
  margin:0 !important;
 }
 .jokeA-mainContent, .jokeA-mainContent + table
 {
  margin:0 0.5em !important;
  padding:0 !important;
 }
 .a-center
 {
  padding:0 !important;
 }
}
