css:
@-moz-document domain(reddit.com)	{
		[id*="arrow"]	{
			background:	#DDEEFF	!important;
			border:	1px solid black	!important;
	}
		[id*="up"]	{
			background:	lime		!important;
			border:	1px dotted black	!important;
	}
		[id*="down"]	{
			background:	orange		!important;
			border:	1px dotted black	!important;
	}
}
