css:
body { background: #290a04 !important; margin: 50px 0px 50px 0px;}
* { font-style: normal;  font-size:12px; line-height:18px}
ul li { list-style:none }
table, td { border: none !important; background:none !important} 
td img { background: #e1c6b0; padding:20px; -moz-border-radius: 8px !important } 
p { text-align: left !important; font-family: lucida grande !important; padding: 0px 10% 10px 10%}
strong { font-weight: normal; color: #e1c6b0 }
a { background: #9f1607; color: #fff !important; padding: 5px 8px; border-radius: 16px; text-decoration:none !important; outline:none !important}
a strong { color: #e1c6b0; }
a:hover { background: #c00 }
a:visited { text-decoration:none !important; background: #4f1105; color: #444 !important; padding: 5px 8px}
a:hover strong { color: #fff }
img { margin-left: 10% }
