@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muusikoiden.net") {

PRE {
    font-size: 12pt !important;
}

H1 {
    font-size: 12pt !important; 
}

A.sidemenu, A:visited.sidemenu {
    font-size: 14pt !important;
}

.quickmenu {
    font-size: 8pt !important;
}

BODY, P, TD, TH, BLOCKQUOTE {
    font-size: 10pt !important;
}

SMALL {
    font-size: 8pt !important; 
}

FONT.msg {
    font-size: 12pt !important;
}

.keskustelu_nick, A.keskustelu_nick:visited, .keskustelu_nick_ban {
    font-size: 12pt !important;
}

.keskustelu_ip {
    font-size: 8pt !important;
}

A.conference_link {
    font-size: 12pt !important;
}

A.thread_link, FONT.thread_link {
    font-size: 12pt !important;
}

.tabtag {
    font-size: 10pt !important;
}

INPUT, SELECT, TEXTAREA {
    font-size: 10pt !important;
}

INPUT.submit, A.button, A.button:visited {
    font-size: 10pt !important;
}

/** End **/
}
