css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wow.com") {
body{background:#000000!important;}
a.header-cobrand, #GH_nav, div.carousel, p.filed-under, div#signin, div#footer, div#container>div#main>div#subcontent,div#grid2,div#legal,div#outerouterslice,div#GH_hat,div#topleader-wrap{display:none!important;}
div#content-container, .postmeta>div.navbar, .post>p.byline,div#content-inner>.searchresults{background:none!important;}
div#content-inner, div#content, div.post>h2{background-color:#ECECEC!important;padding:0px!important;width:100%!important;}
.postmeta{border-bottom:#000000 thick solid}
.postmeta>div.navbar, .postmeta>div.navbar>ul, .post>p.byline{min-height:0px!important;padding:1px 0px 5px!important; margin:0px!important;}
.postmeta>div.navbar>ul>li:nth-child(2n+1){display:none!important;}
div#content-inner>p.page-nav{margin:0px!important; background-color:#000000!important;}
div#comments{background-color:#000000!important;}
div.searchresults>p#paging{margin-top:15px!important;font-size:18px;}
body>div#container>div#main>div#content>div#content-inner>div#content-container>div.post>div.postbody>div[style*="center"],body>div#container>div#main>div#content>div#content-inner>div#content-container>div.post>div.postbody>div.postgallery{display:none}
}
