@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("filemp3.org") {

/* Removes adultfinder at the bottom */
html > body > center > div[align="center"] > iframe {
display: none !important;
}

/* Removes adultfinder ikons in the music download table */
html > body > table.mainouter > tbody > tr > td.outer > table > tbody > tr > td > a > img[src="../pic/couple3.gif"] {
display: none !important;
}

/* Removes Dontate/VIP */
html > body > table.mainouter > tbody > tr > td.outer > table[cellpadding="2"] {
display: none !important;
}

}
