css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(radiko.jp){

/***********************************************************
 * Header
 ***********************************************************/
#globalheader .hbnr img { display: none !important; }
#topspbnr { display: none !important; }
#channel { height: 5px !important; }
#channel:hover { 
  height: 140px !important;
  transition: height 0.2s ease-in-out 0.1s !important;
  background-color: #f8f8f8 !important;
}


/***********************************************************
 * Contents
 ***********************************************************/

#container {
  max-width: none !important;
  width: auto !important;
}

#main {
    float: none !important;
    width: auto !important;
}

.program_table_container .arrowRight,
.program_table_container .arrowLeft {
    position: fixed !important;
    top: 400px !important;
    z-index: 1000 !important;
}

#program_table_container > .program-container {
    width: auto !important;
}

#information {
    margin-left: 30px !important;
}

#top-timeline {
    float: left !important;
    margin-left: 10px !important;
}

}
