@-moz-document domain("vott.ru") {

div {color: #000000 !important;}

div.comment_wrapper, div.comment_wrapper2, div.form_wrapper, div.navigation {border-style: solid !important; border-color: #C3D6B3 !important; border-width: 4px 1px 1px !important; margin-bottom: 4px !important; margin-top: 0px !important;}

div.content_wide {margin-right: 0px !important; margin-left: 0px !important;}

div.content_wrapper {margin-right: 0px !important; padding-top: 0px !important; padding-bottom: 0px !important;}

div.footer {margin-left: inherit !important; margin-right: inherit !important; max-width: inherit !important;}

span.quote {color: #000000 !important; font-style: italic !important;}
}
