css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.daz3d.com") {
/* Change these for backgrounds and general text & link appearance. */
body{ background:#cccccc !important; color:#000000 !important }
a:link{ color:#660033 !important; text-decoration:none !important }
a:hover{ color:#000000 !important; text-decoration:underline !important }

/* Arrangement & Design */
hr{ border:1px solid #999999 !important }
input{ font:11px Verdana,Arial,Helvetica,sans-serif !important }
select{ background:#ffffff !important; font:11px Verdana,Arial,Helvetica,sans-serif !important }
th{ background-image:none !important; color:#000000 !important }

/* Some text size variations */
.admin{ font-size:11px !important; font-weight:bold !important; color:#ff6600 !important }
.bodyline{ width:100% !important; background:#cccccc !important; border:0px !important; }
.cat{ background:#999999 }
.errorline{ background:#999999 !important; border:1px solid #333333 !important}
.explaintitle{ color:#000000 !important; font-size:11px !important; font-weight:bold !important }
.forumline{ background-image:none !important; background:#999999 !important; border:solid 1px #660033 !important }
.genmed{ font-size:11px !important }
.gensmall{ font-size:10px !important }
.helpline{ background:#ffffff !important; border:0 solid !important; font-size:10px !important }
.maintitle,h1{ font-size:18px !important; color:#000000 !important; font-weight:bold !important }
.mod{ font-size:11px !important; font-weight:bold !important; color:#003366 !important }
.name{ font-size:11px !important; font-weight:bold !important }
.nav{ font-size:11px !important; font-weight:bold !important; text-align:left !important }
.post{ background:#ffffff !important; border:1px solid #000000 !important;
 font:11px Verdana,Arial,Helvetica,sans-serif !important }
.postbody{ font-size:12px !important; line-height:125% !important }
.postdetails{ color:#000000 !important; font-size:10px !important }
.row1,.row2,.row3{ background:#cccccc !important }
.rowpic{ background:#999999 }
.quote{ background:#cccccc !important; border:1px solid #660033 !important; color:#000000 !important; font-size:11px !important; line-height:125% !important }
.spacerow{ background:#999999 !important }
.subtitle,h2{ font-size:18px !important; font-weight:bold !important; color:#000000 !important }
.topbkg,.topnav{ background-image:none !important; border-color: #000000 !important; }

}
