@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtorrents.com") {
  img[src="/images/unknown_51.png"],
  img[src="/images/720p_20.png"],
  img[src="/images/720p_51.png"],
  img[src="/images/tv_20.png"] ,
  img[src="/images/tv.png"] {
    position:relative !important;
    top:0 !important;
    left:0 !important;
    height:27px !important;
  }
  tr.torrent_row td a img[width="100"] {
    position:relative;
    top:-27px !important;
    margin-bottom:-27px !important;
  }
}
