	#ctl00_cphHome_rcResultadosComentariosNotas_pnlComentarios {
		height: auto !important;
	}
	#header_banners { display: none !important; }
	#NotaContenido .NotaTexto { font-family: 'Lucida Grande', Helvetica, Arial, sans-serif; font-size: 11px; } 
	#NotaContenido > p, #NotaContenido > center, #NotaContenido .divisor { display: none !important}
	#NotaContenido .NotaRight ins {display: none !important}
	#NotaContenido .titulo { font-family: Helvetica, sans-serif !important; font-weight: bold !important; padding: 0.5em 0;}
	#NotaContenido .NotaInfo {display: none !important}
	#NotaContenido .SiteMap {display: none !important}
	#NotaContenido .NotaOpciones {display: none !important}
	#NotaContenido .areacomentarios .comentarios_advertencia { display: none !important;}
	
	.balazo + div > a,
	.balazo + div + div > a { font-family: Helvetica, sans-serif !important; font-weight: bold !important; font-size: 1.1em}
	
	.notasecundaria span.parrafo,
	.NotaPrimaria span.parrafo,
	.NotaPrimaria div div img + a { font-family: "Lucida Grande" !important; font-size: 0.7em}
	
	.banner_horizontal {display: none !important}
	
