css:
@namespace url(http://www.w3.org/1999/xhtml);

/* 'vbox7 BIG' by waysie */

@-moz-document url-prefix("http://vbox7.com/play"), url-prefix("http://www.vbox7.com/play") {

  html > body > div#mainFrame > div#mainContainer > table > tbody > tr > td:nth-child(3) {
    display: block;
    width: 400px !important;
    margin: 600px 0 0 -401px !important;
  }

  html > body > div#mainFrame > div#mainContainer > table > tbody > tr > td:first-child > table > tbody > tr:nth-last-of-type(1) {
    display: block;
    width: 512px !important;
    background: #f5f5f5;
  }

  html > body > div#mainFrame > div#mainContainer > table > tbody > tr > td:first-child > table > tbody > tr > td > div > embed#VBOXplayer {
    width: 928px !important;
    height: 522px !important;
  }

  div.clipControls {
    width: 918px !important;
  }
}
