css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.yourtv.com.au/guide/) {

body{
background:white !important;
}
a{
color:#2B67AF !important;
}
a:visited{
color:#37567F !important;
}
a:hover{
text-decoration:none !important;
color:#9BB2CF !important;
}
.nav .on{
color:#000000 !important;
}

/* hide crap */
#siteTools, 
#CommonPromoAcrossYourTime,
#register-container,
#msnbtmbanner,
.FIXBOTTOM
{
display:none !important;
}
#_restofday{
display:block;
}
#stripAd{
display:none !important;
}
.printHide{
height:0px !important;
}
#footer-new{
display:none !important;
}
#ninemsn-footer{
display:none !important;
}
#preheader-ninemsn-container{
 display:none !important;
}
#header{
 display:none !important;
}
#pageTitle{
 display:none !important;
}

/* stretch content */
#variablewidth{
width:100% !important;
}
#table_container{
width:100% !important;
}
#header a{
display:block !important;
z-index:5 !important;
}
}
